    x, width = max(20, options()$width - 35), ...
) {
  title <- "persistence block of "
  
  print_step(
    untr_obj = x$terms,
    tr_obj = NULL,
    trained = x$trained,
    title = title,
    width = width
  )
  invisible(x)
}

