#' \itemize{
#'   \item `hom_degree`: Homological degree (type: integer, default: `0L`)
#'   \item `num_bars`: # Bars (persistence pairs) (type: integer, default: `1L`)
#' }
#' 
#' @param hom_degree
#'   The homological degree of the features to be transformed.
#' @param num_bars
#'   Number of bars (persistent pairs) over which to maximize....

#' @import recipes
#' @inheritParams recipes::step_pca
#' @inherit recipes::step_pca return
#' @example inst/examples/zzz-ex-step-vpd-tropical-coordinates.R

#' @export
step_vpd_tropical_coordinates <- function(
    recipe,
    ...,
    role = "predictor",
    trained = FALSE,
    hom_degree = 0L,
    num_bars = 1L,
    columns = NULL,
    keep_original_cols = TRUE,
    skip = FALSE,
    id = rand_id("vpd_tropical_coordinates")
) {
  recipes_pkg_check(required_pkgs.step_vpd_tropical_coordinates())
  
  add_step(
    recipe,
    step_vpd_tropical_coordinates_new(
      terms = rlang::enquos(...),
      trained = trained,
      role = role,
      hom_degree = hom_degree,
      num_bars = num_bars,
