#' @export
tunable.step_vpd_tropical_coordinates <- function(x, ...) {
  tibble::tibble(
    name = c("hom_degree", "num_bars"),
    call_info = list(
      list(pkg = "tdarec", fun = "hom_degree", range = c(0L, unknown())),
      list(pkg = "tdarec", fun = "num_bars", range = c(1L, unknown()))
    ),
    source = "recipe",
    component = "step_vpd_tropical_coordinates",
    component_id = x$id
  )
}

